import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { useDispatch } from "react-redux";
import { clearCart } from "."; // ‚úÖ import action

const Navbar = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch(); // ‚úÖ required before using dispatch

  const user = JSON.parse(localStorage.getItem("userInfo"));

  const handleLogout = () => {
    // üßπ Clear user info and cart
    localStorage.removeItem("userInfo");
    localStorage.removeItem("cart"); // optional if you're persisting cart
    dispatch(clearCart());

    navigate("/login");
  };

  return (
    <nav className="bg-blue-700 text-white flex justify-between items-center px-6 py-4 shadow-md">
      <Link to="/" className="text-2xl font-bold">
        üõçÔ∏è Ecomzy
      </Link>

      <div className="space-x-6 flex items-center">
        <Link to="/" className="hover:underline">
          Home
        </Link>
        <Link to="/cart" className="hover:underline">
          Cart
        </Link>

        {user ? (
          <>
            <span className="font-semibold">Hi, {user.name}</span>
            <button
              onClick={handleLogout}
              className="bg-red-500 px-3 py-1 rounded hover:bg-red-600"
            >
              Logout
            </button>
          </>
        ) : (
          <>
            <Link to="/login" className="hover:underline">
              Login
            </Link>
            <Link to="/register" className="hover:underline">
              Register
            </Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
