import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { removeFromCart, increaseQuantity, decreaseQuantity, clearCart } from '../slices/cartSlice'; // adjust the path

const Cart = () => {
  const dispatch = useDispatch();
  const { items, totalAmount } = useSelector((state) => state.cart);

  if (items.length === 0) {
    return (
      <div className="p-10 text-center">
        <h2 className="text-2xl font-semibold mb-4">ðŸ›’ Your Cart is Empty</h2>
        <p className="text-gray-600">Add some products to your cart to see them here.</p>
      </div>
    );
  }

  return (
    <div className="p-8 max-w-4xl mx-auto bg-white rounded-xl shadow-md mt-6">
      <h2 className="text-3xl font-bold mb-6 text-center">Your Cart</h2>

      {items.map((item) => (
        <div
          key={item.id}
          className="flex items-center justify-between border-b py-4"
        >
          <div className="flex items-center space-x-4">
            <img
              src={item.image}
              alt={item.title}
              className="w-16 h-16 object-contain rounded-md"
            />
            <div>
              <h3 className="font-semibold">{item.title}</h3>
              <p className="text-gray-500">${item.price}</p>
            </div>
          </div>

          <div className="flex items-center space-x-3">
            <button
              onClick={() => dispatch(decreaseQuantity(item.id))}
              className="px-2 py-1 bg-gray-200 rounded hover:bg-gray-300"
            >
              âˆ’
            </button>
            <span>{item.quantity}</span>
            <button
              onClick={() => dispatch(increaseQuantity(item.id))}
              className="px-2 py-1 bg-gray-200 rounded hover:bg-gray-300"
            >
              +
            </button>
          </div>

          <div className="flex items-center space-x-4">
            <p className="font-semibold">
              ${(item.price * item.quantity).toFixed(2)}
            </p>
            <button
              onClick={() => dispatch(removeFromCart(item.id))}
              className="text-red-500 hover:text-red-700"
            >
              Remove
            </button>
          </div>
        </div>
      ))}

      <div className="flex justify-between items-center mt-6">
        <h3 className="text-2xl font-bold">Total: ${totalAmount.toFixed(2)}</h3>
        <button
          onClick={() => dispatch(clearCart())}
          className="bg-red-600 text-white px-6 py-2 rounded-lg hover:bg-red-700 transition-colors"
        >
          Clear Cart
        </button>
      </div>
    </div>
  );
};

export default Cart;
