import React from 'react';

const Item = ({ id, title, price, description, category, image }) => {
  const handleClick = () => {
    // Select the cart div
    const cart = document.querySelector('#cart');
    if (cart) {
      // Create a simple product card in HTML
      const itemHTML = `
        <div class="bg-gray-100 rounded-lg p-2 my-2 flex items-center justify-between">
          <span>${title}</span>
          <span class="font-bold text-blue-700">$${price}</span>
        </div>
      `;
      // Append it to the cart
      cart.innerHTML += itemHTML;
    }
  };

  return (
    <div className="bg-green-300 shadow-md rounded-xl p-4 flex flex-col items-center hover:scale-105 transition-transform duration-200 w-60">
      <img
        src={image}
        alt={title}
        className="w-40 h-40 object-contain mb-4 rounded-md"
      />
      <h2 className="text-lg font-semibold text-center">{title}</h2>
      <p className="text-gray-600 text-sm text-center">{category}</p>
      <p className="text-gray-700 text-sm text-center mt-2 line-clamp-2">{description}</p>
      <p className="font-bold text-blue-700 mt-3">${price}</p>

      <button
        className="mt-4 bg-blue-700 text-white px-4 py-2 rounded-lg hover:bg-blue-800 transition-colors"
        onClick={handleClick}
      >
        Add to Cart
      </button>
    </div>
  );
};

export default Item;
