import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { removeFromCart, clearCart } from "../slices/cartSlices";
import { Link } from "react-router-dom";

const Cart = () => {
  const { items, totalAmount } = useSelector(state => state.cart);
  const dispatch = useDispatch();

  if (items.length === 0)
    return (
      <div className="text-center p-10">
        <h2 className="text-xl font-semibold text-gray-600 mb-4">Your cart is empty üõçÔ∏è</h2>
        <Link to="/" className="text-orange-600 font-bold underline">Continue Shopping</Link>
      </div>
    );

  return (
    <div className="p-8 bg-gray-50 min-h-screen">
      <h2 className="text-2xl font-bold mb-6 text-gray-800">Your Cart</h2>
      {items.map(item => (
        <div key={item.id} className="flex justify-between items-center bg-white p-4 mb-3 rounded-lg shadow">
          <img src={item.image} alt={item.title} className="h-16 object-contain" />
          <h3 className="w-1/3 font-medium">{item.title}</h3>
          <p>${item.price} √ó {item.quantity}</p>
          <button onClick={() => dispatch(removeFromCart(item.id))} className="text-red-600 font-semibold">
            Remove
          </button>
        </div>
      ))}
      <div className="text-right mt-4">
        <h3 className="text-lg font-bold">Total: ${totalAmount.toFixed(2)}</h3>
        <div className="mt-3 space-x-4">
          <button onClick={() => dispatch(clearCart())} className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600">Clear Cart</button>
          <Link to="/checkout" className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700">Checkout</Link>
        </div>
      </div>
    </div>
  );
};

export default Cart;
