import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { useDispatch } from "react-redux";
import { addToCart } from "../slices/cartSlices";

const ProductDetails = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const dispatch = useDispatch();

  useEffect(() => {
    // Fetch product data from fakestoreapi
    axios.get(`https://fakestoreapi.com/products/${id}`)
      .then(res => setProduct(res.data))
      .catch(err => console.error("Error fetching product:", err));
  }, [id]);

  if (!product) {
    return (
      <div className="flex justify-center items-center min-h-screen text-gray-600 text-lg">
        Loading product details...
      </div>
    );
  }

  const handleAddToCart = () => {
    dispatch(addToCart(product));
  };

  return (
    <div className="min-h-screen bg-yellow-50 flex flex-col md:flex-row items-center justify-center p-10">
      <div className="bg-white shadow-xl rounded-xl p-8 flex flex-col md:flex-row gap-10 max-w-5xl w-full">
        
        {/* Product Image */}
        <div className="flex-1 flex justify-center items-center">
          <img
            src={product.image}
            alt={product.title}
            className="h-64 object-contain hover:scale-105 transition-transform"
          />
        </div>

        {/* Product Info */}
        <div className="flex-1 flex flex-col justify-between">
          <div>
            <h1 className="text-3xl font-bold text-gray-800 mb-3">
              {product.title}
            </h1>
            <p className="text-gray-500 italic mb-2">{product.category}</p>
            <p className="text-gray-700 mb-5 leading-relaxed">
              {product.description}
            </p>
          </div>

          <div>
            <p className="text-2xl font-bold text-orange-600 mb-4">
              ${product.price}
            </p>
            <button
              onClick={handleAddToCart}
              className="bg-orange-600 text-white px-6 py-3 rounded-lg font-semibold hover:bg-orange-700 transition"
            >
              ðŸ›’ Add to Cart
            </button>
          </div>
        </div>

      </div>
    </div>
  );
};

export default ProductDetails;
